import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./SelectedInput.scss";
SelectedInput.propTypes = {
  field: PropTypes.object.isRequired,
  form: PropTypes.object.isRequired,

  dataSource: PropTypes.array,
  disabled:PropTypes.bool,
};
SelectedInput.defaultProps = {
  dataSource: [],
  disabled:false,
};
function SelectedInput(props) {
  const { field, form, dataSource, disabled } = props;
  const { errors, touched } = form;
  const { name, value } = field;
  const showError = errors[name] && touched[name];
  const showValidField = touched[name] && !errors[name];
  
  const [selected, setSelected] = useState(value);

  function handleEventChange(selectedOption) {
    const selectedValue = selectedOption
      ? selectedOption.value
      : selectedOption;
    const dataChange = {
      target: {
        name: name,
        value: selectedValue,
      },
    };
    field.onChange(dataChange);
    setSelected(selectedValue);
  }
  return (
    <div className="ContainerInput">
      <div
        className={
          showValidField
            ? "ContainerInput InputValid"
            : showError
            ? "ContainerInput InputInvalid"
            : "ContainerInput"
        }
      >
        <div className="Input">
          <select
            {...field}
            onChange={(event) => handleEventChange(event.target)}
            disabled={disabled}
            defaultValue={dataSource[0].id}
          >
            {dataSource.map((item) => {
              const keys = Object.keys(item);
              return (
                <option
                  key={item[keys[0]]}
                  value={item[keys[0]]}
                  selected={item[keys[0]] === selected}
                >
                  {item[keys[1]]}
                </option>
              );
            })}
          </select>
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        <div className="Error">
          <FontAwesomeIcon className="IconInvalid" icon="exclamation-circle" />
          <span>{showError && errors[name]}</span>
        </div>
      </div>
    </div>
  );
}

export default SelectedInput;
