import "./App.css";
import "./assets/scss/common.scss";

// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import { Provider } from "react-redux";
import store from "./store";
//dynamic icon

import { library } from "@fortawesome/fontawesome-svg-core";
import * as Icons from "@fortawesome/free-solid-svg-icons";
import HeaderMain from './component/HeaderMain/HeaderMain';
import Footer from './component/Footer/Footer';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useLocation,
} from "react-router-dom";
import Admin from './screens/Admin/Admin';
import UserLayout from './screens/UserLayout/UserLayout';

const iconList = Object.keys(Icons)
  .filter((key) => key !== "fas" && key !== "prefix")
  .map((icon) => Icons[icon]);
library.add(...iconList);

function App() {
  const { pathname ='' } = useLocation();
  return (
    <Provider store={store}>
      <div className="App">
        {/* //get all icon name */}
        {/* {iconList.map(icon=>{
          return (
            <FontAwesomeIcon style={{padding:'5px'}} icon={icon.iconName}>
              {icon.iconName}
            </FontAwesomeIcon>
          );
        })} */}
        <Router>
          <HeaderMain />
          <Switch>
            <Redirect from="/" to="/home" />
            <Route path="/home" component={UserLayout} />
            <Route path="/admin" component={Admin} />

            <Route path="/about" exact>
              <About />
            </Route>
            <Route path="/product" exact>
              <Product />
            </Route>
            <Route path="/product/:id">
              <DetailProduct />
            </Route>
            <Route path="/contact">
              <Contact />
            </Route>
            <Route path="/news">
              <News />
            </Route>
            <Route path="/" exact>
              <Home />
            </Route>
            <Route path="/source">
              <ExampleSource />
            </Route>
            <Route path="/cart-detail">
              <CartDetail />
            </Route>
            <Route path="/search-result">
              <ResultSearch />
            </Route>
            <Route path="/my-order">
              <MyOrder />
            </Route>
            <Route path="/account">
              <Login />
            </Route>
          </Switch>
          <Footer />
        </Router>
      </div>
    </Provider>
  );
}

export default App;
