import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Input.scss";

Input.propTypes = {
  type: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
  onChange: PropTypes.func,
  onFocus: PropTypes.func,
  onBlur: PropTypes.func,
  control:PropTypes.object.isRequired,
};

Input.defaultProps = {
  onChange: null,
  onFocus: null,
  onBlur: null,
  validationField: null,
};
function Input(props) {
  const { type, name, placeholder, onChange, onFocus, onBlur, control } = props;
  

  function handleEventChange(event) {
    if (onChange) {
      onChange(event);
    }
  }

  function handleEventFocus(event) {
    if (onFocus) {
      onFocus(event);
    }
  }

  function handleEventBlur(event) {
    if (onBlur) {
      onBlur(event);
    }
  }
  return (
    <div className="Input">
      <div
        className={
          !control || control.hasError === null
            ? "ContainerInput"
            : control.hasError === true
            ? "ContainerInput InputInvalid"
            : "ContainerInput InputValid"
        }
      >
        <div className="Input">
          <input
            type={type}
            name={name}
            placeholder={placeholder}
            onChange={(event) => handleEventChange(event)}
            onFocus={(event) => handleEventFocus(event)}
            onBlur={(event) => handleEventBlur(event)}
          />
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        <div className="Error">
          <FontAwesomeIcon className="IconInvalid" icon="exclamation-circle" />
          <span>{control && control.msg ? control.msg : ""}</span>
        </div>
      </div>
    </div>
  );
}

export default Input;
