import React from 'react';
import PropTypes from 'prop-types';
import './Steper.scss';
import * as Icon from "@fortawesome/react-fontawesome";
import {
  faChevronCircleDown,
} from "@fortawesome/free-solid-svg-icons";
Steper.propTypes = {
  listSteper:PropTypes.array.isRequired,
};

function Steper(props) {
  const { listSteper } = props;
  return (
    <div className="Steper">
      {listSteper.map((steper,index)=>{
        return (
          <div className={steper.isActive ? "ActiveSteperItem" : "SteperItem"}>
            <div className="IconItem">
              <FontAwesomeIcon icon={Icon[steper.iconName]} />
            </div>
            <div className="TitleItem">
              <h3>Giỏ hàng của tôi</h3>
            </div>
            <div className="LineStatus">
              <span className="ItemStatus">1</span>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Steper;
