    <div className="Container">
      <div className="SubContainer">
        <div className="Layout">
          {listProduct.map((item, index) => {
            return (
              <CardProduct
                key={index}
                itemProduct={item}
                addCardClick={clickBuyProduct}
              />
            );
          })}
