        <div className="DataStructure">
          <p>
            import CardProduct from "../../component/CardProduct/CardProduct";
          </p>
          <p>//Khai báo hàm xử lí ở parent function clickBuyProduct(item)</p>
          <pre>{showDataExample(listProduct)}</pre>
        </div>
      </div>

