          <Pagination
            limit={3}
            currentPage={1}
            itemPage={5}
            totalItem={60}
            pageChangeClick={getPagination}
          />
        </div>
        <div className="DataStructure">
          <p>import Steper from "../../component/Steper/Steper";</p>
          <p>//Khai báo hàm xử lí ở parent function getItemSeleted(item)</p>
          <pre>{showDataExample(listSteper)}</pre>
          <hr />
