import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Input.scss";

Input.propTypes = {
  type: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
  formGroup: PropTypes.func.isRequired,
  onChange: PropTypes.func,
  onFocus: PropTypes.func,
  onBlur: PropTypes.func,
  validationField: PropTypes.func,
};

Input.defaultProps = {
  onChange: null,
  onFocus: null,
  onBlur: null,
  validationField: null,
};
function Input(props) {
  const {
    type,
    name,
    placeholder,
    onChange,
    onFocus,
    onBlur,
    validationField,
    formGroup
  } = props;
  const [error, setError] = useState({ hasError: null, msg: "" });
  const [value, setValue] = useState('');
  formGroup(forms=>{
    forms.push({ ...error, value: value });
  });

  function handleEventChange(value) {
    setValue(value);
    if (onChange) {
      onChange(value);
    }
  }

  function handleEventFocus(value) {
    if (onFocus) {
      onFocus(value);
    }
  }

  function handleEventBlur(value) {
    if (onBlur) {
      onBlur(value);
    }
    if (validationField) {
      setError(validationField(value));
    }
  }
  return (
    <div className="Input">
      <div
        className={
          !error || error.hasError === null
            ? "ContainerInput"
            : error.hasError === true
            ? "ContainerInput InputInvalid"
            : "ContainerInput InputValid"
        }
      >
        <div className="Input">
          <input
            type={type}
            name={name}
            placeholder={placeholder}
            onChange={(event) => handleEventChange(event.target.value)}
            onFocus={(event) => handleEventFocus(event.target.value)}
            onBlur={(event) => handleEventBlur(event.target.value)}
          />
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        <div className="Error">
          <FontAwesomeIcon className="IconInvalid" icon="exclamation-circle" />
          <span>
            {error && error.msg? error.msg : ""}
          </span>
        </div>
      </div>
    </div>
  );
}

export default Input;
