import { StaticRouter } from "react-router-dom";

const initialState = {
  idCustomer: "5d1b8222-8241-45dd-ac89-70d1793b6f4a",
  cartList: [],
  address: {},
  addressOther: {},
  deliveryMethod: {},
  paymentMethod: {},
  deliveryFee: 0,
  intoMoney: 0,
  totalPay: 0,
  status: "draf",
};
const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_ADDRESS": {
      state.address = { ...action.payload };
      return state;
    }
    case "ADD_ADDRESS_OTHER": {
      state.addressOther = { ...action.payload };
      return state;
    }
    case "ADD_DELIVERY_METHOD": {
      state.deliveryMethod = { ...action.payload };
      return state;
    }
    case "ADD_PAYMENT_METHOD": {
      state.paymentMethod = { ...action.payload };
      return state;
    }
    case "ADD_ITEM_TO_CART": {
      let product = state.cartList.find((cart) => {
        return cart.id === action.payload.id;
      });

      if (!!product) {
        product.amount += 1;
      } else {
        state.cartList.push({ ...action.payload, amount: 1 });
      }

      const obj = { ...state, cartList: [...state.cartList] };
      sessionStorage.setItem("item_cart", JSON.stringify(obj));
      return state;
    }
    
    case "UPDATE_AMOUNT_CART": {
      if (!!action.payload) {
        StaticRouter.cartList[action.payload.index] = action.payload;
        const obj = { ...state, cartList: [...state.cartList] };
        sessionStorage.setItem("item_cart", JSON.stringify(obj));
      }
      return state;
    }
    case "REMOVE_CART": {
      if (!!action.payload) {
        state.cartList.splice(action.payload.index, 1);
        state.cartList = [...state.cartList];
        const obj = { ...state, cartList: [...state.cartList] };
        sessionStorage.setItem("item_cart", JSON.stringify(obj));
      }
      return state;
    }

    default:
      return state;
  }
};

export default cartReducer;

