import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Input.scss";

Input.propTypes = {
  field: PropTypes.object.isRequired,
  form: PropTypes.object.isRequired,

  type: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
  disable: PropTypes.bool,
};

Input.defaultProps = {
  onChange: null,
  onFocus: null,
  onBlur: null,
  disable:false,
};
function Input(props) {
  const { type, name, placeholder, onChange, onFocus, onBlur, control } = props;
  const { value, setValue } = useState(control.value);

  function handleEventChange(event) {
    if (onChange) {
      onChange(event);
      setValue(event.target.value);
    }
  }

  function handleEventFocus(event) {
    if (onFocus) {
      onFocus(event);
      setValue(event.target.value);
    }
  }

  function handleEventBlur(event) {
    if (onBlur) {
      console.log(event);
      onBlur(event);
      setValue(event.target.value);
    }
  }
  return (
    <div className="Input">
      <div
        className={
          !control || control.hasError === null
            ? "ContainerInput"
            : control.hasError === true
            ? "ContainerInput InputInvalid"
            : "ContainerInput InputValid"
        }
      >
        <div className="Input">
          <input
            type={type}
            name={name}
            placeholder={placeholder}
            value={value}
            onChange={(event) => handleEventChange(event)}
            onFocus={(event) => handleEventFocus(event)}
            onBlur={() => handleEventBlur(control)}
          />
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        {control.errors.map((error, index) => {
          <div className="Error" key={index}>
            <FontAwesomeIcon
              className="IconInvalid"
              icon="exclamation-circle"
            />
            <span>{error && error.msg ? error.msg : ""}</span>
          </div>;
        })}
      </div>
    </div>
  );
}

export default Input;
