import React from 'react';
import PropTypes from 'prop-types';
import './OrderDetailPrint.scss';
OrderDetailPrint.propTypes = {
  refPrint:PropTypes.object,
};

OrderDetailPrint.defaultProps = {
  refPrint:null,
};

function OrderDetailPrint(props) {
  const { refPrint } = props;
  const date =  new Date();
  const day = (`0${date.getDate()}`).substr(-2);
  const month = (`0${date.getMonth()+1}`).substr(-2);
  const year = date.getFullYear();
  return (
    <div className="OrderDetailPrint" ref={refPrint}>
      <style>{`@media print {.OrderDetailPrint {
  display: flex;
  flex: 1;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  .Header {
    display: flex;
    width: 100%;
    flex-direction: row;
    padding: 5px 20px;
    .LogoCompany {
      display: flex;
      flex: 1;
      justify-content: center;
      align-items: center;
    }
    .InfoCompany {
      display: flex;
      flex: 3;
      flex-direction: column;
      .Group {
        display: flex;
        flex: 1;
        flex-direction: row;
        .Label {
          font-weight: bold;
          margin-right: 15px;
        }
      }
    }
  }
  .InfoBuyer {
    display: flex;
    flex: 1;
    flex-direction: column;
    width: 93%;
    padding:20px;
    .Group {
      display: flex;
      flex: 1;
      flex-direction: row;
      .Label {
        font-weight: bold;
        margin-right: 15px;
      }
    }
  }
}}`}</style>
      <h3>Thông tin đơn hàng</h3>
      <h4>
        Ngày (date) {day} tháng (month) {month} năm (year) {year}.
      </h4>
      <div className="Header">
        <div className="LogoCompany">
          <img
            src={require("../../assets/images/logo.png").default}
            alt="Logo Company"
          />
        </div>
        <div className="InfoCompany">
          <div className="Group">
            <span className="Label">Địa chỉ (Address):</span>
            <span className="Content">50/2 Bình Tân, Bình Mỹ, Châu Phú</span>
          </div>
          <div className="Group">
            <span className="Label">Điện thoại (Tel):</span>
            <span className="Content">0777 203 042</span>
          </div>
          <div className="Group">
            <span className="Label">Số tài khoản (Account No.):</span>
            <span className="Content">12345665</span>
          </div>
        </div>
      </div>
      <div className="InfoBuyer">
        <div className="Group">
          <span className="Label">Họ tên người mua hàng (Name of buyer):</span>
          <span className="Content">Nguyễn Văn A</span>
        </div>
        <div className="Group">
          <span className="Label">Địa chỉ (Address):</span>
          <span className="Content">50/2 Bình Tân, Bình Mỹ, Châu Phú</span>
        </div>
        <div className="Group">
          <span className="Label">Điện thoại (Tel):</span>
          <span className="Content">0777 203 042</span>
        </div>
        <div className="Group">
          <span className="Label">Hình thức thanh toán (Payment term):</span>
          <span className="Content">COD</span>
        </div>
      </div>
      <div className="Order"></div>
      <div className="Footer"></div>
    </div>
  );
}

export default OrderDetailPrint;
