import React from 'react';
import PropTypes from 'prop-types';
import './Table.scss';
Table.propTypes = {
  dataList:PropTypes.array.isRequired,
  headerList:PropTypes.array.isRequired,
};

function Table(props) {
  const { dataList, headerList } = props;
  return (
    <div className="TableProduct">
      <div className="HeaderTable">
        {headerList.map((header, index) => {
          return <div key={index} style={{flex:header.flex}}>{header.label}</div>;
        })}
      </div>
      {dataList.map((product, index) => {
        return (
          <div className="ContentTable" key="index">
            {product.map(dataProduct=>{
              return (
                <div style={{ flex: headerList[index].flex }}>{index + 1}</div>
              );
            })}
            
          </div>
        );
      })}
    </div>
  );
}

export default Table;
