import React from "react";
import PropTypes from "prop-types";
import "./Table.scss";
import {TYPE_COLUMN_TABLE,ACTION_TYPE_TABLE} from "../../Constant/Constant.js";
Table.propTypes = {
  dataList: PropTypes.array.isRequired,
  headerList: PropTypes.array.isRequired,
};

function Table(props) {
  const { dataList, headerList } = props;
  return (
    <div className="TableProduct">
      <div className="HeaderTable">
        {headerList.map((header, index) => {
          return (
            <div key={index} style={{ flex: header.flex }}>
              {header.label}
            </div>
          );
        })}
      </div>
      {dataList.map((item,index) => {
        return (
          <div className="ContentTable" key={index}>
            {headerList.map((keyHeader, ind) => {
              return (
                <div key={`CT${index}${ind}`} style={{ flex: keyHeader.flex }}>
                  {keyHeader.hasOwnProperty("propertyMapping")
                    ? renderComponentByType(keyHeader, item, (event) => {
                        console.log(
                          "Checkbox change",
                          event.event.target.checked
                        );
                      })
                    : ""}
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}


