import React, { useState } from "react";
import PropTypes from "prop-types";
import "./PayInfo.scss";
PayInfo.propTypes = {
  deliveryMethod: PropTypes.array.isRequired,
  payMethod: PropTypes.array.isRequired,
  changeDeliveryMethod: PropTypes.func,
  changePayMethod: PropTypes.func,
};
PayInfo.defaultProps = {
  changeDeliveryMethod: null,
  changePayMethod: null,
};
function PayInfo(props) {
  const { deliveryMethod, payMethod, changeDeliveryMethod, changePayMethod } =props;
  const { deliverySelected, setDeliverySelected } = useState(null);
  const { payMethodSelected, setPayMethodSelected } = useState(payMethod[0]);

  function handleChangeDeliveryMethod(valueSelected) {
    if (changeDeliveryMethod) {
      const item = deliveryMethod.filter(item=>{item.id === valueSelected});
      changeDeliveryMethod(item);
      setDeliverySelected(item);
    }
  }

  function handleChangePayMethod(valueSelected) {
    if (changePayMethod) {
      const item = deliveryMethod.filter((item) => {
        item.id === valueSelected;
      });
      changePayMethod(item);
      setPayMethodSelected(item);
    }
  }
  return (
    <div className="PayInfo">
      <div className="HeaderContainer">
        <h3 className="HeaderContent">2. Thanh toán và vận chuyển</h3>
      </div>
      <div className="ContentContainer">
        <h3 className="HeaderContent">Thông tin thanh toán</h3>
        <div className="DeliveryContainer">
          <select onChange={(eveten) => handleChangeDeliveryMethod(eveten.target.value)}>
            {deliveryMethod.map((delivery) => {
              return (
                <option
                  key={delivery.id}
                  value={delivery.id}
                  selected={delivery.id}
                >
                  {delivery.title}
                </option>
              );
            })}
          </select>
        </div>
        <h3 className="HeaderContent">Thanh toán</h3>
        <div className="DeliveryContainer">
          <span>
            <input type="radio" name="deliveryPay" defaultChecked={true} />
            <span>Thanh toán khi giao hàng (COD)</span>
          </span>
          <span>
            <input type="radio" name="deliveryPay" />
            <span>Thanh toán online qua cổng OnePay bằng thẻ ATM nội địa</span>
          </span>
          <span>
            <input type="radio" name="deliveryPay" />
            <span>Chuyển khoản qua ngân hàng</span>
          </span>
          <span>
            <input type="radio" name="deliveryPay" />
            <span>
              Thanh toán online qua cổng OnePay bằng thẻ Visa/Master/JCB
            </span>
          </span>
        </div>
      </div>
    </div>
  );
}

export default PayInfo;
