import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./InputText.scss";
import useForm from "../../customhook/useForm";
InputText.propTypes = {
  type: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
  onChange: PropTypes.func,
  onFocus: PropTypes.func,
  onBlur: PropTypes.func,
  validationField: PropTypes.func,
};

InputText.defaultProps = {
  onChange: null,
  onFocus: null,
  onBlur: null,
  validationField: null,
};
function InputText(props) {
  const {
    type,
    name,
    placeholder,
    onChange,
    onFocus,
    onBlur,
    validationField,
  } = props;
  const [error, setError] = useState({ hasError: null, msg: "" });
  function handleEventChange(value) {
    if (onChange) {
      onChange(value);
    }
    setError({ hasError: true, msg: "Error roi" });
  }

  function handleEventFocus(value) {
    if (onFocus) {
      onFocus(value);
    }
    setError({ hasError: null, msg: "" });
  }

  function handleEventBlur(value) {
    if (onBlur) {
      onBlur(value);
    }
    setError({ hasError: false, msg: "" });
  }
  return (
    <div className="InputText">
      <div
        className={
          error.hasError === null
            ? "ContainerInput"
            : error.hasError === true
            ? "ContainerInput InputInvalid"
            : "ContainerInput InputValid"
        }
      >
        <div className="Input">
          <input
            type={type}
            name={name}
            placeholder={placeholder}
            onChange={(event) => handleEventChange(event.target.value)}
            onFocus={(event) => handleEventFocus(event.target.value)}
            onBlur={(event) => handleEventBlur(event.target.value)}
          />
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        <div className="Error">
          <FontAwesomeIcon className="IconInvalid" icon="exclamation-circle" />
          <span>{error.msg}</span>
        </div>
      </div>
    </div>
  );
}

export default InputText;
