import React from 'react';
import PropTypes from 'prop-types';
import './Steper.scss';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import {
//   faAngleRight,
//   faList,
//   faChevronCircleDown,
// } from "@fortawesome/free-solid-svg-icons";



Steper.propTypes = {
  listSteper:PropTypes.array.isRequired,
};

function Steper(props) {
  const { listSteper } = props;
  return (
    <div className="Steper">
      {listSteper.map((steper,index)=>{
        return (
          <div className={steper.isActive ? "ActiveSteperItem" : "SteperItem"}>
            <div className="IconItem">
              <FontAwesomeIcon icon="faList" />
            </div>
            <div className="TitleItem">
              <h3>{steper.title}</h3>
            </div>
            <div className="LineStatus">
              <span className="ItemStatus">{index}</span>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Steper;
