import React from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleRight,
  faList,
  faChevronCircleDown,
} from "@fortawesome/free-solid-svg-icons";
import { Link, useRouteMatch } from "react-router-dom";
import "./NavigateMenu.scss";

NavigateMenu.propTypes = {
  listCategory: PropTypes.array,
  listMenu: PropTypes.array,
};

NavigateMenu.defaultProps = {
  listCategory: PropTypes.array,
  listMenu: PropTypes.array,
};
function NavigateMenu(props) {
  return (
    <div className="NavMenu">
      <div className="ContainerCategory">
        <div className="TitleCategory">
          <FontAwesomeIcon icon={faList} className="IconCategory" />
          DANH MUC SAN PHAM
          <FontAwesomeIcon
            icon={faChevronCircleDown}
            className="IconChevronCircleDown"
          />
        </div>
        <ul>
          <li>
            Máy ảnh &amp; Máy quay phim
            <FontAwesomeIcon icon={faAngleRight} className="IconRight" />
            <div className="SubMenu">
              <div className="ContentSubMenu">
                <h5>MÁY QUAY PHIM</h5>
                <ul>
                  <li>Máy quy phim sony</li>
                  <li>Máy quay phim panasonic </li>
                  <li>Máy quy phim sony</li>
                </ul>
                <h5>MÁY ẢNH SỐ</h5>
                <ul>
                  <li>Máy quy phim sony</li>
                  <li>Máy quay phim panasonic </li>
                  <li>Máy quy phim sony</li>
                  <li>Máy quy phim sony</li>
                  <li>Máy quay phim panasonic</li>
                  <li>Máy quy phim sony</li>
                </ul>
              </div>
              <div className="ContentSubMenu">
                <h5>BỘ ĐỒ THỂ THAO NAM</h5>
                <ul>
                  <li>Máy quy phim sony</li>
                  <li>Máy quay phim panasonic </li>
                  <li>Máy quy phim sony</li>
                </ul>
              </div>
            </div>
          </li>
          <li>
            Máy để bàn &amp; Laptop
            <FontAwesomeIcon icon={faAngleRight} className="IconRight" />
            <ul className="SubMenu">
              <li> Máy ảnh &amp; Máy quay phim</li>
            </ul>
          </li>
          <li>
            Điện thoại
            <FontAwesomeIcon icon={faAngleRight} className="IconRight" />
          </li>
          <li>
            Máy tính bảng
            <FontAwesomeIcon icon={faAngleRight} className="IconRight" />
          </li>
        </ul>
      </div>

      <div className="Menu">
        <ul>
          <MenuItem
            item={{ path: "/about", label: "Gioi thieu", exact: false }}
            key={0}
          />
          <MenuItem
            item={{ path: "/product", label: "San pham", exact: false }}
            key={1}
          />
          <MenuItem
            item={{ path: "/contact", label: "Lien he", exact: false }}
            key={2}
          />
          <MenuItem
            item={{ path: "/news", label: "Tin tuc", exact: false }}
            key={3}
          />
          <MenuItem
            item={{ path: "/", label: "Trang chu", exact: true }}
            key={4}
          />
        </ul>
      </div>
    </div>
  );
}

function MenuItem({ item }) {
  const match = useRouteMatch({ path: item.path });
  return (
    <li className={match && match.isExact ? "Active" : ""}>
      <Link
        to={item.path}
        exact={item && item.exact ? item.exact.toString() : "false"}
        className="MenuLink"
      >
        {item.label}
      </Link>
    </li>
  );
}
export default NavigateMenu;
