import React from "react";
import PropTypes from "prop-types";
import Header from "../../component/Header/Header";
import CardProduct from "../../component/CardProduct/CardProduct";
import Pagination from "../../component/Pagination/Pagination";
import VerticalMenu from "../../component/VerticalMenu/VerticalMenu";
import FeaturedNews from "../../component/FeaturedNews/FeaturedNews";
import NavigateQuickly from "../../component/NavigateQuickly/NavigateQuickly";
import CardNews from "../../component/CardNews/CardNews";
import Steper from "../../component/Steper/Steper";
import DeliveryAddress from "../../component/DeliveryAddress/DeliveryAddress";
import PayInfo from "../../component/PayInfo/PayInfo";
import OrderInfo from "../../component/OrderInfo/OrderInfo";
import OrderDetail from "../../component/OrderDetail/OrderDetail";
import Table from "../../component/Table/Table";

import "./Product.scss";

Product.propTypes = {};
//Product
const listProduct = [
  {
    id: 1,
    productName: "ASUS TP550LA-CJ04",
    price: 25000000,
    discount: 0,
    isHot: false,
    imageUrl: require("../../assets/images/camera.jpg").default,
  },
  {
    id: 2,
    productName: "ASUS TP550LA-CJ04",
    price: 25000000,
    discount: 10,
    isHot: false,
    imageUrl: require("../../assets/images/camera.jpg").default,
  },
  {
    id: 3,
    productName: "ASUS TP550LA-CJ04",
    price: 25000000,
    discount: 0,
    isHot: true,
    imageUrl: require("../../assets/images/camera.jpg").default,
  },
  {
    id: 4,
    productName: "ASUS TP550LA-CJ04",
    price: 25000000,
    discount: 0,
    isHot: false,
    imageUrl: require("../../assets/images/camera.jpg").default,
  },
];

//Feature News
const listDataNews = [
  {
    id: "1",
    urlImage: require("../../assets/images/camera.jpg").default,
    title: "Trên tay Dell XPS 13 (2015): mỏng hơn, mạnh hơn, pin tốt hơn",
  },
  {
    id: "2",
    urlImage: require("../../assets/images/camera.jpg").default,
    title: "Panasonic ra mắt bộ 3 máy ảnh Compact mới",
  },
  {
    id: "3",
    urlImage: require("../../assets/images/camera.jpg").default,
    title: "Trên tay Dell XPS 13 (2015)",
  },
];

//NavigateQuickly
const listMenuQuick = [
  {
    id: "1",
    label: "Trang chu",
    path: "#",
  },
  {
    id: "2",
    label: "San pham",
    path: "#",
  },
  {
    id: "3",
    label: "Chi tiet san pham",
    path: "#",
  },
];

// Steper
const listSteper = [
  {
    isActive: true,
    iconName: "angle-double-down",
    title: "Giỏ hàng của tôi",
  },
  {
    isActive: true,
    iconName: "list",
    title: "Thanh toán",
  },
  {
    isActive: false,
    iconName: "list",
    title: "Hoàn tất",
  },
];

//orderDetail
const orderDetail = {
  idOrder: "#100000326",
  orderDate: "26/06/2021",
  payMethod: "Thanh toán khi giao hàng (COD)",
  listProduct: [
    {
      id: "1",
      name: 'Asus TP550LA-CJ040H - Core i3 4030U / 15.6" Touch / 4GB (Đen)',
      price: "10.950.000",
      amount: 1,
      intoMoney: "10.950.000",
    },
    {
      id: "2",
      name: 'Asus TP550LA-CJ040H - Core i3 4030U / 15.6" Touch / 4GB (Đen)',
      price: "10.950.000",
      amount: 1,
      intoMoney: "10.950.000",
    },
  ],
  intoMoney: "10.990.000",
};

// Data Table Example
const headerList = [
  { flex: 5, label: "TÊN SẢN PHẨM", propertyMapping: "name" },
  { flex: 2, label: "GIÁ", propertyMapping: "price" },
  { flex: 2, label: "SỐ LƯỢNG", propertyMapping: "amount" },
  { flex: 3, label: "THÀNH TIỀN", propertyMapping: "intoMoney" },
  { flex: 1, label: "", propertyMapping: "name" },
];

const dataList = [
  {
    id: "P00001",
    name: 'Asus TP550LA-CJ040H - Core i3 4030U / 15.6" Touch / 4GB (Đen)',
    price: "10.950.000",
    amount: 1,
    intoMoney: "10.950.000",
  },
  {
    id: "P00002",
    name: 'Asus TP550LA-CJ040H - Core i3 4030U / 15.6" Touch / 4GB (Đen)',
    price: "10.950.000",
    amount: 1,
    intoMoney: "10.950.000",
  },
];
// End Data Table Example
function Product(props) {
  //Product
  function clickBuyProduct(item) {
    console.log(item);
  }

  //Paginaion
  function getPagination(item) {
    console.log(item);
  }

  //Feature News
  function getItemSeleted(item) {
    console.log("Item feature news:", item);
  }

  return (
    <div>
      {/* Table */}
      <Table dataList={dataList} headerList={headerList} />

      {/* Header */}
      <Header title="San pham" />

      {/* NavigateQuickly */}
      <NavigateQuickly listQuickLink={listMenuQuick} />

      {/* OrderDetail */}
      <OrderDetail orderDetail={orderDetail}></OrderDetail>

      {/* DeliveryAddress */}
      <DeliveryAddress />

      {/* PayInfo */}
      <PayInfo />

      {/* OrderInfo */}
      <OrderInfo />

      {/* Steper */}
      <Steper listSteper={listSteper} />

      {/* CardNews */}
      <CardNews></CardNews>

      {/* Product */}
      <div className="Container">
        {listProduct.map((item, index) => {
          return (
            <CardProduct
              key={index}
              itemProduct={item}
              addCardClick={clickBuyProduct}
            />
          );
        })}
      </div>

      {/* VerticalMenu */}
      <VerticalMenu />

      {/* Pagination */}
      <Pagination
        limit={3}
        currentPage={1}
        itemPage={5}
        totalItem={60}
        pageChangeClick={getPagination}
      />

      {/* FeaturedNews */}
      <FeaturedNews
        header="tin tức nổi bật"
        itemList={listDataNews}
        selectedItemEvent={getItemSeleted}
      />
    </div>
  );
}

export default Product;
