import React from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleDoubleLeft,
  faAngleDoubleRight,
} from "@fortawesome/free-solid-svg-icons";

Pagination.propTypes = {
  currentPage: PropTypes.number,
  itemPage: PropTypes.number,
  totalItem: PropTypes.number,
  pageChangeClick: PropTypes.func,
};
Pagination.defaultProps = {
  currentPage: 0,
  itemPage: 0,
  totalItem: 0,
  pageChangeClick: null,
};
function Pagination(props) {
  const { currentPage, itemPage, totalItem, pageChangeClick } = props;
  const totalPage = Math.floor(totalItem / itemPage);
  const paginationRender = [
    <Button>
      <FontAwesomeIcon icon={FaAngleDoubleLeft} />
    </Button>,
  ];
  for (let ind = 1; ind <= totalPage; ind++) {
    <span>{ind}</span>;
  }
  paginationRender.push(
    <Button>
      <FontAwesomeIcon icon={FaAngleDoubleRight} />
    </Button>
  );

  return paginationRender;
}

export default Pagination;
