import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./SelectedInput.scss";
SelectedInput.propTypes = {
  field: PropTypes.object.isRequired,
  form: PropTypes.object.isRequired,

  dataSource: PropTypes.array.isRequired,
};

function SelectedInput(props) {
  const { field, form, dataSource } = props;
  const { errors, touched } = form;
  const { name, value } = field;
  const showError = errors[name] && touched[name];
  const showValidField = touched[name] && !errors[name];
  const [selectedValue, setSelectedValue] = useState(value);
  function handleEventChange(value) {
    field.onChange(value);
    setSelectedValue(value);
  }
  return (
    <div className="ContainerInput">
      <div
        className={
          showValidField
            ? "ContainerInput InputValid"
            : showError
            ? "ContainerInput InputInvalid"
            : "ContainerInput"
        }
      >
        <div className="Input">
          <select onChange={(event) => handleEventChange(event.target.value)}>
            {dataSource &&
              dataSource.map((item) => {
                return (
                  <option
                    key={item.value}
                    value={item.value}
                    selected={selectedValue === item.value}
                  >
                    {item.label}
                  </option>
                );
              })}
          </select>
          <FontAwesomeIcon className="IconValid" icon="check" />
        </div>
        <div className="Error">
          <FontAwesomeIcon className="IconInvalid" icon="exclamation-circle" />
          <span>{showError && errors[name]}</span>
        </div>
      </div>
    </div>
  );
}

export default SelectedInput;
