import React, { useState } from 'react';
import NavigateQuickly from './../../component/NavigateQuickly/NavigateQuickly';
import Header from './../../component/Header/Header';
import './MyOrder.scss';
import Input from './../../component/Input/Input';
import { Field, Formik, Form } from "formik";
import { Button } from '@material-ui/core';
import Table from './../../component/Table/Table';
import orderDetailApi from '../../api/orderDetail';

function MyOrder() {
  const [orderDetail,setOrderDetail] = useState({});
  const [showOrderDetail, setShowOrderDetail] = useState(false);
  const submitSearch = (value)=>{
    
    orderDetailApi.getorderDetailById(value.idOrder).then(result=>{
    
      if (result.hasOwnProperty("id")) {
        setShowOrderDetail(true);
        setOrderDetail(result);
          console.log(result);
      } else {
        setShowOrderDetail(false);
      }
    });
  }
  const initialValue = {
    idOrder:''
  };
  return (
    <div className="MyOrder">
      <div className="ContainerBody">
        <div className="ContainerBodyLeft"></div>
        <div className="ContainerBodyRight">
          <div className="NavQuickly">
            <NavigateQuickly />
          </div>
          <div className="Header">
            <Header title="Kiểm tra đơn hàng" />
          </div>
          <div className="ContainerSearchOrder">
            <Formik
              initialValues={initialValue}
              onSubmit={(value) => submitSearch(value)}
            >
              <Form className="FormInfo">
                <label htmlFor="idOrder">Nhập mã đơn hàng</label>
                <Field
                  name="idOrder"
                  placeholder="VD: 1231212"
                  component={Input}
                  type="text"
                />
                <Button className="Button" type="submit">
                  Xem ngay
                </Button>
              </Form>
            </Formik>
          </div>
          <div className="ContainerOrderDetail">
            {showOrderDetail ?(<div className="ContainerHeader">
              <Header title="Thông tin đơn hàng" />
              <div className="ContainerContent">
                <span>
                  Đơn hàng của <span className="CustomerName">Nguyen A</span>
                  <span className="IdOrder">(#100000351)</span>
                  lúc 2021-07-15T19:56:32.757
                </span>
                <Table dataList={[]} headerList={headerList} />
              </div>
            </div>):(<span>Không tìm thấy đơn hàng của bạn. Vui lòng kiểm tra lại thông tin.</span>)}
            
          </div>
        </div>
      </div>
    </div>
  );
}

export default MyOrder;

const headerList = [
  {
    flex: 1,
    label: "",
    propertyMapping: "imageUrl",
    type: "IMAGE",
    formatCurrency: false,
  },
  {
    flex: 3,
    label: "TÊN SẢN PHẨM",
    propertyMapping: "productName",
    type: "LABLE",
    formatCurrency: false,
  },
  {
    flex: 2,
    label: "GIÁ",
    propertyMapping: "price",
    type: "LABLE",
    formatCurrency: true,
  },
  {
    flex: 2,
    label: "SỐ LƯỢNG",
    propertyMapping: "amount",
    type: "LABLE",
    formatCurrency: false,
  },
  {
    flex: 2,
    label: "THÀNH TIỀN",
    propertyMapping: "intoMoney",
    type: "LABLE",
    formatCurrency: true,
  },
  {
    flex: 1,
    label: "",
    actionType: ["DELETE"],
    type: "ACTION",
  },
];
