import { useState } from "react";

function useForm(formGroup) {
  const [isSubmit, setSubmit] = useState(null);
  const [form, setForm] = useState({});
  const [valueOnBlur, setValueOnBlur] = useState({value: "",validations: [],errors: [],hasError: null});

  if (isSubmit) {
    isSubmit.preventDefault();
  }
  // setValueOnBlur(event=>{
  //   console.log(event);
  // });
  return { setSubmit, setForm, setValueOnBlur };
}

export default useForm;
