import { useState } from "react";

function useForm() {
  const [isSubmit, setSubmit] = useState(null);
  const [form, setForm] = useState([]);
  if (isSubmit) {
    isSubmit.preventDefault();
    for (let i = 0; i < isSubmit.target.length; i++) {
      if (isSubmit.target[i].type !== "submit") {
        // isSubmit.target[i].onBlur();
        console.log(isSubmit.target[i]["data"]);
      }
    }
  }
  return { setSubmit, setForm };
}

export default useForm;
