import React, { useState } from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAngleDoubleLeft,
  faAngleDoubleRight,
} from "@fortawesome/free-solid-svg-icons";
import "./Pagination.scss";
Pagination.propTypes = {
  currentPage: PropTypes.number,
  itemPage: PropTypes.number,
  totalItem: PropTypes.number,
  pageChangeClick: PropTypes.func,
};
Pagination.defaultProps = {
  currentPage: 0,
  itemPage: 0,
  totalItem: 0,
  pageChangeClick: null,
};
function Pagination(props) {
  const { currentPage, itemPage, totalItem, pageChangeClick } = props;
  const totalPage = Math.floor(totalItem / itemPage);
  const { pagination, setPagination } = useState({});
  const { current, setCurrent } = useState(currentPage);
  function changePagination(indexPage) {
    if (pageChangeClick) {
      pageChangeClick(pagination);
    }
  }

  function nextClick() {
    setCurrent(current + 1);
  }

  function prevClick() {
    setCurrent(current - 1);
  }
  const paginationRender = [
    <span className="NextIndex" onClick={() => nextClick()} key={0}>
      <FontAwesomeIcon icon={faAngleDoubleLeft} className="IconPagination" />
    </span>,
  ];
  for (let ind = 1; ind <= totalPage; ind++) {
    paginationRender.push(
      <span
        key={ind}
        className={ind === currentPage ? "PageIndex Active" : "PageIndex"}
        onClick={() => changePagination(ind)}
      >
        {ind}
      </span>
    );
  }
  paginationRender.push(
    <span className="PrevIndex" onClick={() => prevClick()} key={11}>
      <FontAwesomeIcon icon={faAngleDoubleRight} className="IconPagination" />
    </span>
  );

  return <div className="Pagination">{paginationRender}</div>;
}

export default Pagination;
