import React, { useRef, useState } from 'react';
import PropTypes from 'prop-types';

ToDoList.propTypes = {
  listToDo: PropTypes.array,
  clickToItem: PropTypes.func
};

ToDoList.defaultProps = {
  listToDo: [],
  clickToItem: null,
}


function ToDoList(props) {
  const { listToDo, clickToItem } = props;
  const [color, setColor] = useState('red')
  const colorPrev = useRef();
  function handleEventClick(item) {
    if (clickToItem) {
      item.active = true;
      clickToItem(item);
      setColor('yellow');
    }
  }

  return (
    <ul>
      {listToDo.map(item =>
        <li style={{ color: item.active ? color : colorPrev.current }} key={item.id} onClick={() => handleEventClick(item)}>{item.title}</li>
      )}
    </ul>
  );
}

export default ToDoList;
